import { useEffect, useState } from "react";   
import Items from "./Items";
import Search from './Search';
import axios from "axios";
export default function EquipmentsList() {
    const [items, setItems] = useState([])
    const [searchText, setSearchText] = useState()

    const filterItem = async()=>{
        const result = await items.filter((item)=> item.name.toLowerCase().includes(searchText.toLowerCase()));
        console.log(result);
        setItems(result);
    }

    const handleReset = (data)=>{
        setSearchText(data)
        if(data.length <1){
            fetchData(items);
        }
    }

    const fetchData = async()=>{
        const {data: {results}} = await axios({
            url: 'https://www.dnd5eapi.co/api/equipment',
            method: 'GET'
        })
        setItems(results);
    }

    useEffect(()=>{
         fetchData();
    }, [])

    return(
        <div className="wrapper">
            <Search  handleReset={handleReset} filterItem = {filterItem} fetchData={fetchData}/>
            <div id="list--header">Equipments</div>
            <Items 
                items={items}
            />
        </div>
    )
};
