import { useState } from 'react'
import { MdSearch } from 'react-icons/md'
export default function Search({ items, setItems }) {

    const [searchText, setSearchText] = useState()


    const filterItem = async(data)=>{
        const result = await items.filter((item)=> item.name.toLowerCase() ===data.toLowerCase());
        setItems(result)

    }

    return(
        <div className="search">
            <input 
            type="text"
            placeholder='Search...' />
            <MdSearch className='search--icons' 
            size='1.3em' 
            onClick={(event) => filterItem(event.target.value)}
            />
        </div>
    )
};
