import { useState } from 'react'
import { MdSearch } from 'react-icons/md'
export default function Search({ items, setItems }) {

    const [searchText, setSearchText] = useState()


    const filterItem = async()=>{
       
        const result = await items.filter((item)=> item.name.toLowerCase() === searchText.toLowerCase());
        console.log(result);
        setItems(result);

    }

    console.log(searchText);

    return(
        <div className="search">
            <input 
            type="text"
            placeholder='Search...' onChange={(data)=> setSearchText(data.target.value)}/>
            <MdSearch className='search--icons' 
            size='1.3em' 
            onClick={() => filterItem()}
            />
            <button>Reset</button>
        </div>
    )
};
