import { useEffect, useState } from 'react'
import { MdSearch } from 'react-icons/md'
export default function Search({ items, setItems, fetchData }) {

    const [searchText, setSearchText] = useState()


    const filterItem = async()=>{
       
        const result = await items.filter((item)=> item.name.toLowerCase() === searchText.toLowerCase());
        console.log(result);
        setItems(result);
    }

    const handleReset = (data)=>{
        setSearchText(data)
        if(data.length <1){
            fetchData(items);
        }
    }
    

    return(
        <div className="search">
            <input 
            type="text"
            placeholder='Search...' onChange={(data)=> handleReset(data.target.value)}/>
            <MdSearch className='search--icons' 
            size='1.3em' 
            onClick={() => filterItem()}
            />
            <button onClick={()=>fetchData()}>Reset</button>
        </div>
    )
};
